from machine import Pin, SoftI2Cimport networkimport ssd1306import timefrom time import sleepsta_if = network.WLAN(network.STA_IF); sta_if.active(True)# ESP32 Pin assignment i2c = SoftI2C(scl=Pin(22), sda=Pin(21))# ESP8266 Pin assignment#i2c = SoftI2C(scl=Pin(5), sda=Pin(4))oled_width = 128oled_height = 64oled = ssd1306.SSD1306_I2C(oled_width, oled_height, i2c)oled.text('Hello, World 1!', 0, 0)oled.text('Hello, World 2!', 0, 10)oled.text('Hello, World 3!', 0, 20)        oled.show()sta_if.scan()oled.text('2', 10, 10)oled.show()time.sleep(1)oled.fill(0)oled.text('Conectando...!', 0, 0)sta_if.connect('wIFRN-IoT','deviceiotifrn')oled.text('1', 10, 10)oled.show()time.sleep(1)oled.fill(0)import webreploled.text('Hello, World 2!', 0, 10)oled.text('Hello, World 3!', 0, 20)oled.text('BIPES', 0, 30)oled.show()time.sleep(1)oled.fill(0)oled.text('MicroPython', 0, 0)oled.text('Password: bipes', 0, 10)x=sta_if.config('essid')oled.text(str(x), 0, 40)oled.text('Go BIPES', 0, 20)x=sta_if.ifconfig()oled.text(str(x), 0, 50)oled.show()#sta_if.isconnected()