import networkimport urequestsimport timewlan = network.WLAN(network.STA_IF)wlan.active(True)wlan.connect("BENVENUTO", "zeliaeluis")print()while True:  if(wlan.isconnected()):    print("Connected")  url = "http://192.168.0.113:3000/"  # Here we build a request URL  response = urequests.get(url)    response_json = response.json()  application = response_json['Aplication']  temp = response_json['Temperatura']  umidade = response_json['Umidade']  # Print out the complete response  print("Aplicao: ", application)  print("Temperatura: ", temp)  print("Umidade: ", umidade)  print()  time.sleep(1)