import socketimport networkimport timeprint("Connecting to WiFi", end="")wlan = network.WLAN(network.STA_IF)wlan.active(True)wlan.connect("BENVENUTO", "zeliaeluis")while not wlan.isconnected():  print(".", end="")  time.sleep(0.1)print(" Connected!")print(wlan.ifconfig())addr = socket.getaddrinfo('192.168.0.113', 12345)[0][-1]s = socket.socket()s.connect(addr)while True:    data = s.recv(500)    print(str(data, 'utf8'), end='')